package lecClass;

public class exercise2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k = 20;
		int j = 20;
		int i;
		while (k>10) {
			System.out.println("K = "+k);
			k--;
		}
		
		do {
			System.out.println("j = "+j);
			j--;
		}
		while(j>10);
			
		for(i=20; i>10; i--) {
			System.out.println("i = "+i);
		}
	}

}
